#!/bin/bash
set -e -x

cat >vagrant.yml<<EOF
#cloud-config
ssh_authorized_keys:
  - ssh-rsa  vagrant insecure public key
rancher:
  console: debian
  resize_device: /dev/sda
  services:
    salt-minion:
      image: mhdali/salt-minion:jessie
      restart: always
      privileged: true
      net: host
      pid: host
      uts: host
      labels:
        - io.rancher.os.after=console
      volumes:
        - /opt:/opt
        - /opt/salt-minion:/etc/salt
        - /usr/bin/docker:/usr/bin/docker
        - /run/docker.sock:/run/docker.sock
write_files:
  - path: /opt/salt-minion/minion
    permissions: "0644"
    owner: root
    content: |
      master: 172.19.9.1
  - path: /root/.ssh/authorized_keys
    permissions: "0700"
    owner: root
    content: |
      ssh-rsa  vagrant insecure public key
EOF

sudo ros install -f -d /dev/sda -c ./vagrant.yml
